# function to make rounding means and sd easier
rndmean <- function(clm) {
  return(round(mean(clm),2))
}
rndsd <- function(clm) {
  return(round(sd(clm),2))
}

newdata <- read.csv("newdata.csv")

signif="***"
notsig="n.s."

sigfunc <- function(pval) {
if (pval < 0.05){
  label = signif
} else {
    label = notsig
}
  return (label)
}
